mapper.update:
UPDATE COMMENTS
SET RECOMMEND_COUNT = RECOMMEND_COUNT + 1
WHERE COMMENT_ID = #{commentId}

mapper.select:
SELECT COMMENT_ID, POST_ID, USERNAME, CONTENT, CREATED_DATE, RECOMMEND_COUNT
FROM COMMENTS
WHERE COMMENT_ID = #{commentId}

mapper.update:
UPDATE COMMENTS
SET CONTENT = #{content}
WHERE COMMENT_ID = #{commentId}
